import { Box, Button, FormControl, Input, Modal, ModalBody, ModalContent, ModalFooter, ModalHeader, ModalOverlay, useToast } from "@chakra-ui/react";
import { axios } from "axios";
import { useState } from "react";
import { ChatState} from "../Context/GlobalHooks";

function GroupChatModal(){
     
    const { user } = ChatState();
    const toast = useToast();
    const {GroupChatName,setGroupChatName} = useState();
    const {search,setSearch} = useState("");
    const {loading,setLoading} = useState(false);
    const { searchResult,setSearchResult} = useState([]);
    const { selectedUsers, setSelectedUsers}= useState([]);

    const handleSearch = async (query)=>{
        setSearch(query);
        if(!query){
            return;
        }
        try {
            setLoading(true);
            const config = {
                headers:{
                    Authorization : `Bearer ${user.token}`,   
                },
            }

            const { data } = await axios.get(`/api/user?search=${search}`,config);
            
            searchResult( data );
            setLoading(false);
        } catch (error) {
           toast({
             title: "Error Occured!",
             description: "Failed to Load the Search Results",
             status: "error",
             duration: 5000,
             isClosable: true,
             position: "bottom-left",
           }); 
        }
    }


    const handleGroup =  async (newuser)=>{
         setSel
    }

    return (
      <>
        <span onClick={onOpen}>{children}</span>

        <Modal onClose={onClose} isOpen={isOpen} isCentered>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader
              fontSize="35px"
              fontFamily="Work sans"
              d="flex"
              justifyContent="center"
            >
              Create Group Chat
            </ModalHeader>
            <ModalCloseButton />
            <ModalBody d="flex" flexDir="column" alignItems="center">
              <FormControl>
                <Input
                  placeholder="Chat Name"
                  mb={3}
                  onChange={(e) => setGroupChatName(e.target.value)}
                />
              </FormControl>
              <FormControl>
                <Input
                  placeholder="Add Users eg: John, Piyush, Jane"
                  mb={1}
                  onChange={(e) => handleSearch(e.target.value)}
                />
              </FormControl>
              <Box w="100%" d="flex" flexWrap="wrap">
                {selectedUsers.map((u) => (
                  <UserBadgeItem
                    key={u._id}
                    user={u}
                    handleFunction={() => handleDelete(u)}
                  />
                ))}
              </Box>
              {loading ? (
                // <ChatLoading />
                <div>Loading...</div>
              ) : (
                searchResult
                  ?.slice(0, 4)
                  .map((user) => (
                    <UserListItem
                      key={user._id}
                      user={user}
                      handleFunction={() => handleGroup(user)}
                    />
                  ))
              )}
            </ModalBody>
            <ModalFooter>
              <Button onClick={handleSubmit} colorScheme="blue">
                Create Chat
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    );
}

export default GroupChatModal;
