import { Box, Button, Input, Text, Tooltip, useDisclosure, useToast } from "@chakra-ui/react";
import { SearchIcon , ChevronDownIcon, BellIcon } from "@chakra-ui/icons";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
} from "@chakra-ui/react";
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from "@chakra-ui/react";
import { useState } from "react";
import axios from "axios";
import ChatLoading from "../Avatar/ChatLoading";
import UserList from "../Avatar/UserList";

function Toppart(){

     const toast = useToast();
     const { isOpen, onOpen, onClose } = useDisclosure();
     const [search,setSearch] = useState();
     const [loading,setLoading] =useState(false);
     const [searchResult,setSearchResult] = useState([]);
     const user = JSON.parse(localStorage.getItem("userInfo"));

     const handleSearch = async () => {
      if (!search) {
        toast({
          title: "Please Enter something in search",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "top-left",
        });
        return;
      }

       try {
        setLoading(true);
       
        const config = {
          header: {
            Authorization: `Bearer ${
              user.toke}`,
          },
        };

        const { data } = await axios.get(`/api/user?search=${search}`,config);

        setLoading(false);
        setSearchResult( data );
       } catch (error) {
          toast({
            title: "Error Occured!",
            description: "Failed to Load the Search Results",
            status: "error",
            duration: 5000,
            isClosable: true,
            position: "bottom-left",
          });
       }
     }


   return (
     <>
       <Box
         display="flex"
         justifyContent="space-between"
         alignItems="center"
         bg="white"
         w="100%"
         p="5px 10px 5px 10px"
         borderWidth="5px"
       >
         <Tooltip label="Search the users">
           <Button variant="ghost" onClick={onOpen}>
             <SearchIcon />
             <Text d={{ base: "none", md: "flex" }} px={4}>
               Search User
             </Text>
           </Button>
         </Tooltip>
         <Text fontSize="2xl" fontFamily="Work sans">
           Chat App
         </Text>
         <div>
           <Menu>
             <MenuButton p={1}>
               <BellIcon fontSize="2xl" m={1} />
             </MenuButton>
           </Menu>
           <Menu>
             <MenuButton as={Button} rightIcon={<ChevronDownIcon />}>
               Actions
             </MenuButton>
             <MenuList>
               <MenuItem>Profile</MenuItem>
               <MenuItem>Logout</MenuItem>
             </MenuList>
           </Menu>
         </div>
       </Box>

       <Drawer isOpen={isOpen} placement="right" onClose={onClose}>
         <DrawerOverlay />
         <DrawerContent>
           <DrawerCloseButton />
           <DrawerHeader>Search Users</DrawerHeader>

           <DrawerBody>
             <Box d="flex" pb={2}>
               <Input
                 placeholder="Search by name or email"
                 mr={2}
                 value={search}
                 onChange={(e) => setSearch(e.target.value)}
               />
               <Button onClick={handleSearch}>Go</Button>
             </Box>
             {loading ? (
              <ChatLoading />
              ) : (
                searchResult?.map((user)=>(
                    <UserList 
                      key={user._id}
                      user={user}
                    />
                ))
              )}
           </DrawerBody>

           <DrawerFooter>
             <Button variant="outline" mr={3} onClick={onClose}>
               Cancel
             </Button>
           </DrawerFooter>
         </DrawerContent>
       </Drawer>
     </>
   );
}

export default Toppart;
