import { ArrowBackIcon, ViewIcon } from "@chakra-ui/icons";
import { Box, Button, FormControl, IconButton, Input, Spinner, Text, useToast } from "@chakra-ui/react";
import axios from "axios";
import { useEffect, useState } from "react";
import UpdateGroupChatModal from "../Avatar/GroupSettings";
import ProfileModal from "../Avatar/ProfileBadge";
import { ChatState } from "../Context/GlobalHooks";

function SingleChat({ fetchAgain , setFetchAgain }) {
  const { user, selectedChat, setSelectedChat } = ChatState();

  const [messages , setMessages] = useState([]);
  const [loading,setLoading]=useState(false);
  const [newMessage,setNewMessage] = useState("");
  const toast = useToast();

  const fetchMessages =async()=>{

  }

  useEffect(()=>{
    fetchMessages();
  },[setSelectedChat])

  useEffect(()=>{
    setFetchAgain(!fetchAgain);
  },[])

  const sendMessage = async (event)=>{
     if( event.key === "Enter" && newMessage){
         try {
            
            const config = {
              headers: {
                "Content-type": "application/json",
                Authorization: `Bearer ${user.token}`,
              },
            };

            const { data } = await axios.post(`/api/message`,{
                content:newMessage,
                chatId:selectedChat._id,
            },config);

            setMessages([...messages,data]);
            setNewMessage("");

         } catch (error) {
            toast({
              title: "Error Occured!",
              description: "Failed to send the Message",
              status: "error",
              duration: 5000,
              isClosable: true,
              position: "bottom",
            });
         }
     }
  }

  const typingHandler= (e)=>{
    setNewMessage(e.target.value);
  }

  return (
    <>
      {selectedChat ? (
        <>
          <Text
            fontSize={{ base: "28px", md: "30px" }}
            pb={3}
            px={2}
            w="100%"
            fontFamily="Work sans"
            display="flex"
            justifyContent={{ base: "space-between" }}
            alignItems="center"
          >
            <IconButton
              display={{ base: "flex", md: "none" }}
              icon={<ArrowBackIcon />}
              onClick={() => setSelectedChat("")}
            />
            {!selectedChat.isGroupChat ? (
              <>
                {selectedChat.users[1].name}
                <ProfileModal user={selectedChat.users[1]}>
                  <IconButton display={{ base: "flex" }} icon={<ViewIcon />} />
                </ProfileModal>
              </>
            ) : (
              <>
                {selectedChat.chatname.toUpperCase()}
                <UpdateGroupChatModal
                  fetchAgain={fetchAgain}
                  setFetchAgain={setFetchAgain}
                />
              </>
            )}
          </Text>
          <Box
            d="flex"
            flexDir="column"
            justifyContent="flex-end"
            p={3}
            bg="#E8E8E8"
            w="100%"
            h="100%"
            borderRadius="lg"
            overflowY="hidden"
          >
            {loading ? (
              <Spinner
                size="xl"
                w={20}
                h={20}
                alignSelf="center"
                margin="auto"
              />
            ) : (
              <div className="messages">
                <h1>ALL MESSAGES ARE SHOWN HERE</h1>
                {/* <ScrollableChat messages={messages} /> */}
              </div>
            )}

            <FormControl
              display="flex"
              onKeyDown={sendMessage}
              id="first-name"
              isRequired
              mt={3}
            >
              <Input
                variant="filled"
                bg="#E0E0E0"
                placeholder="Enter a message.."
                value={newMessage}
                onChange={typingHandler}
              />
              <Button type="submit" marginLeft="8px" colorScheme="whatsapp" onClick={sendMessage}>Send</Button>
            </FormControl>
          </Box>
        </>
      ) : (
        <Box
          display="flex"
          alignItems="center"
          justifyContent="center"
          h="100%"
        >
          <Text fontSize="3xl" pb={3} fontFamily="Work sans">
            Click on a user to start chatting
          </Text>
        </Box>
      )}
    </>
  );
}

export default SingleChat;
