import { Avatar, Box, Button, Input, Spinner, Text, Tooltip, useDisclosure, useToast } from "@chakra-ui/react";
import { SearchIcon , ChevronDownIcon, BellIcon } from "@chakra-ui/icons";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
} from "@chakra-ui/react";
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from "@chakra-ui/react";
import { useState } from "react";
import axios from "axios";
import ChatLoading from "../Avatar/ChatLoading";
import UserList from "../Avatar/UserList";
import ProfileModal from "../Avatar/ProfileBadge";
import { useNavigate } from "react-router-dom";
import { ChatState} from "../Context/GlobalHooks";

function Toppart(){

     const toast = useToast();
     const { isOpen, onOpen, onClose } = useDisclosure();
     const [search,setSearch] = useState();
     const [loading,setLoading] =useState(false);
     const [searchResult,setSearchResult] = useState([]);
     const [loadingChat, setLoadingChat] = useState(false);
     const navigate = useNavigate();

     const {
       setSelectedChat,
       user,
       notification,
       setNotification,
       chats,
       setChats,
     } = ChatState();

     const logoutHandler = () => {
       localStorage.removeItem("userInfo");
       navigate('/');
     };

     const handleSearch = async () => {
      if (!search) {
        toast({
          title: "Please Enter something in search",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "top-left",
        });
        return;
      }

       try {
        setLoading(true);
       
         const config = {
           headers: {
             Authorization: `Bearer ${user.token}`,
           },
         };

        const { data } = await axios.get(`/api/user?search=${search}`,config);

        setLoading(false);
        setSearchResult( data );
       } catch (error) {
          toast({
            title: "Error Occured!",
            description: "Failed to Load the Search Results",
            status: "error",
            duration: 5000,
            isClosable: true,
            position: "bottom-left",
          });
       }
     }

     const accesschats = async (userId)=>{
         setLoadingChat(true);

         const config = {
            header:{
                "Content-Type": "application/json",
                Authorization : `Bearer ${user.token}`,
            }
         }

         const { data } = await axios.post("/api/")
     }

   return (
     <>
       <Box
         display="flex"
         justifyContent="space-between"
         alignItems="center"
         bg="white"
         w="100%"
         p="5px 10px 5px 10px"
         borderWidth="5px"
       >
         <Tooltip label="Search the users">
           <Button variant="ghost" onClick={onOpen}>
             <SearchIcon />
             <Text d={{ base: "none", md: "flex" }} px={4}>
               Search User
             </Text>
           </Button>
         </Tooltip>
         <Text fontSize="2xl" fontFamily="Work sans">
           Chat App
         </Text>
         <div>
           <Menu>
             <MenuButton p={1}>
               <BellIcon fontSize="2xl" m={1} />
             </MenuButton>
           </Menu>
           <Menu>
             <MenuButton as={Button} rightIcon={<ChevronDownIcon />}>
               <Avatar
                 size="sm"
                 cursor="pointer"
                 name={user.name}
                 src={user.pic}
               />
             </MenuButton>
             <MenuList>
               <ProfileModal user={user}>
                 <MenuItem>Profile</MenuItem>
               </ProfileModal>
               <MenuItem onClick={logoutHandler}>Logout</MenuItem>
             </MenuList>
           </Menu>
         </div>
       </Box>

       <Drawer isOpen={isOpen} placement="left" onClose={onClose}>
         <DrawerOverlay />
         <DrawerContent>
           <DrawerCloseButton />
           <DrawerHeader borderBottomWidth="1px">Search Users</DrawerHeader>

           <DrawerBody>
             <Box display="flex" pb={2}>
               <Input
                 placeholder="Search by name or email"
                 mr={2}
                 value={search}
                 onChange={(e) => setSearch(e.target.value)}
               />
               <Button onClick={handleSearch}>Go</Button>
             </Box>
             {loading ? (
               <>
                 <Spinner ml="auto" display="flex" />
                 <ChatLoading />
               </>
             ) : (
               searchResult?.map((user) => (
                 <UserList key={user._id} user={user} />
               ))
             )}
           </DrawerBody>

           <DrawerFooter>
             <Button variant="outline" mr={3} onClick={onClose}>
               Cancel
             </Button>
           </DrawerFooter>
         </DrawerContent>
       </Drawer>
     </>
   );
}

export default Toppart;
