import { ViewIcon } from "@chakra-ui/icons";
import { IconButton, ModalOverlay,ModalContent,ModalHeader,ModalCloseButton,ModalBody,Box,FormControl,Input,Button,ModalFooter, useToast, useDisclosure } from "@chakra-ui/react";
import UserBadgeItem from "./UserBadgeItem";
import UserListItem from "./UserList";
import {ChatState} from "../Context/GlobalHooks";
import axios from "axios";
import { useState } from "react";

function UpdateGroupChatModal( { fetchAgain , setFetchAgain }){
    
    const toast = useToast();
    const { user, selectedChat , setSelectedChat} = ChatState();
     const { isOpen, onOpen, onClose } = useDisclosure();
    const[loading,setLoading] = useState(false);
    const[groupChatName, setGroupChatName] = useState();
    const [renameloading , setRenameLoading] = useState(false);
    const [search, setSearch]= useState();
    const [searchResult, setSearchResult] = useState([]);

    const handleAddUser = async(usertoadd)=>{
        if (selectedChat.users.find((u) => u._id === usertoadd._id)) {
          toast({
            title: "User Already in group!",
            status: "error",
            duration: 5000,
            isClosable: true,
            position: "bottom",
          });
          return;
        }

        if (selectedChat.groupAdmin._id !== user._id) {
          toast({
            title: "Only admins can add someone!",
            status: "error",
            duration: 5000,
            isClosable: true,
            position: "bottom",
          });
          return;
        }

        
    }

    const handleSearch = async (query) => {
      setSearch(query);
      if (!query) {
        return;
      }

      try {
        setLoading(true);

        const config = {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        };

        const { data } = await axios.get(`/api/user?search=${search}`, config);

        setLoading(false);
        setSearchResult(data);
      } catch (error) {
        console.log(error);
        toast({
          title: "Error Occured!",
          description: "Failed to Load the Search Results",
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "bottom-left",
        });
      }
    };


    const handleRename = async()=>{
        if(!groupChatName){
            return;
        }

        try {
            setRenameLoading(true);

            const config = {
                headers:{
                    Authorization:`Bearer ${user.token}`,
                },
            }

            const { data } = await axios.put(`/ap/chat/rename`,
            {
               chatId: selectedChat._id,
               chatname: groupChatName,
            },config)

            setSelectedChat( data );
            setFetchAgain(!fetchAgain);
            setRenameLoading(false);
        } catch (error) {
             toast({
               title: "Error Occured!",
               description: error.response.data.message,
               status: "error",
               duration: 5000,
               isClosable: true,
               position: "bottom",
             });
             setRenameLoading(false);
        }
        setGroupChatName("");
    }

    const handleRemove = async(usertoremove)=>{
     if(selectedChat.groupAdmin._id !== user._id && usertoremove._id !== user._id ){
       toast({
         title: "Only admins can remove someone!",
         status: "error",
         duration: 5000,
         isClosable: true,
         position: "bottom",
       });
       return;
     }
     try {
        setLoading(true);
       const config = {
         headers: {
            Authorization: `Bearer ${user.token}`,
         },
       };

       const {data} = await axios.put(`/api/chat/groupremove`,
       {
        chatId: selectedChat._id,
        userId: usertoremove._id,
       }
       ,config) 

       usertoremove._id === user._id ? setSelectedChat() : setSelectedChat(data);
       setFetchAgain(!fetchAgain);
       setLoading(false);
     } catch (error) {
        toast({
          title: "Error Occured!",
          description: error.response.data.message,
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
        setLoading(false);
     }
     setGroupChatName("");
    }

    return (
      <>
        <IconButton d={{ base: "flex" }} icon={<ViewIcon />} onClick={onOpen} />

        <Modal onClose={onClose} isOpen={isOpen} isCentered>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader
              fontSize="35px"
              fontFamily="Work sans"
              d="flex"
              justifyContent="center"
            >
              {selectedChat.chatname}
            </ModalHeader>

            <ModalCloseButton />
            <ModalBody d="flex" flexDir="column" alignItems="center">
              <Box w="100%" d="flex" flexWrap="wrap" pb={3}>
                {selectedChat.users.map((u) => (
                  <UserBadgeItem
                    key={u._id}
                    user={u}
                    admin={selectedChat.groupAdmin}
                    handleFunction={() => handleRemove(u)}
                  />
                ))}
              </Box>
              <FormControl d="flex">
                <Input
                  placeholder="Chat Name"
                  mb={3}
                  value={groupChatName}
                  onChange={(e) => setGroupChatName(e.target.value)}
                />
                <Button
                  variant="solid"
                  colorScheme="teal"
                  ml={1}
                  isLoading={renameloading}
                  onClick={handleRename}
                >
                  Update
                </Button>
              </FormControl>
              <FormControl>
                <Input
                  placeholder="Add User to group"
                  mb={1}
                  onChange={(e) => handleSearch(e.target.value)}
                />
              </FormControl>

              {loading ? (
                <Spinner size="lg" />
              ) : (
                searchResult?.map((user) => (
                  <UserListItem
                    key={user._id}
                    user={user}
                    handleFunction={() => handleAddUser(user)}
                  />
                ))
              )}
            </ModalBody>
            <ModalFooter>
              <Button onClick={() => handleRemove(user)} colorScheme="red">
                Leave Group
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    );
}

export default UpdateGroupChatModal;
