import { Input,InputGroup, InputRightElement, FormControl, FormLabel, VStack, Button } from "@chakra-ui/react";
import { useState } from "react";
import axios from "axios";
import { useToast } from "@chakra-ui/react";
import { useNavigate } from "react-router-dom";

const Signup = async ()=>{
    const [name,setName]= useState();
    const [show, setShow] = useState(false);
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const[pic, setPic] = useState();
    const [confirmpassword,setConfirmpassword]= useState();
    const [loading, setLoading] = useState(false);
    const handleClick = () => setShow(!show);
    const toast = useToast();
    const navigate = useNavigate();

    const postDetails = (pics) => {
      setLoading(true);
      if (pics === undefined) {
        toast({
          title: "Please Select an Image!",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
        return;
      }
      console.log(pics);
      if (pics.type === "image/jpeg" || pics.type === "image/png") {
        const data = new FormData();
        data.append("file", pics);
        data.append("upload_preset", "chat-app");
        data.append("cloud_name", "pankajcoder");
        fetch("https://api.cloudinary.com/v1_1/pankajcoder/image/upload", {
          method: "post",
          body: data,
        })
          .then((res) => res.json())
          .then((data) => {
            setPic(data.url.toString());
            console.log(data.url.toString());
            setLoading(false);
          })
          .catch((err) => {
            console.log(err);
            setLoading(false);
          });
      } else {
        toast({
          title: "Please Select an Image!",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
        setLoading(false);
        return;
      }
    };

    const submitHandler = async ()=>{
        setLoading(true);
        if(!name || !password || !email || !confirmpassword){
           toast({
             title: "Enter the  all required Fields",
             status: "warning",
             duration: 5000,
             isClosable: true,
             position: "bottom",
           });
           setLoading(false);
           return; 
        }
         if (password !== confirmpassword) {
           toast({
             title: "Passwords Do Not Match",
             status: "warning",
             duration: 5000,
             isClosable: true,
             position: "bottom",
           });
           return;
         }
        try {
            
            const config = {
                header:{
                    "Content-Type": "application/json",
                },
            }

            const {data} = await axios.post("/api/user",{name , email ,password ,pic,},config);
             toast({
               title: "Registration Successful",
               status: "success",
               duration: 5000,
               isClosable: true,
               position: "bottom",
             });
             localStorage.setItem("userinfo",JSON.stringify(data));
             setLoading(false);
             navigate('/chats');
        } catch (error) {
           toast({
             title: "Error Occured!",
             description: error.response.data.message,
             status: "error",
             duration: 5000,
             isClosable: true,
             position: "bottom",
           });
           setLoading(false); 
        }
    }

    return (
      <VStack spacing="10px">
        <FormControl id="name" isRequired>
          <FormLabel>Name</FormLabel>
          <Input
            value={name}
            type="text"
            placeholder="Enter your name"
            onChange={(e) => setName(e.target.value)}
          />
        </FormControl>
        <FormControl id="email" isRequired>
          <FormLabel>Email</FormLabel>
          <Input
            type="email"
            value={email}
            placeholder="Enter your email address"
            onChange={(e) => setEmail(e.target.value)}
          />
        </FormControl>
        <FormControl id="password" isRequired>
          <FormLabel>Password</FormLabel>
          <InputGroup>
            <Input
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              type={show ? "text" : "password"}
              placeholder="Enter password"
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={handleClick}>
                {show ? "Hide" : "Show"}
              </Button>
            </InputRightElement>
          </InputGroup>
        </FormControl>
        <FormControl id="password" isRequired>
          <FormLabel>Confirm Password</FormLabel>
          <InputGroup size="md">
            <Input
              value={confirmpassword}
              type={show ? "text" : "password"}
              placeholder="Confirm password"
              onChange={(e) => setConfirmpassword(e.target.value)}
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={handleClick}>
                {show ? "Hide" : "Show"}
              </Button>
            </InputRightElement>
          </InputGroup>
        </FormControl>
        <FormControl id="pic">
          <FormLabel>Upload your pic</FormLabel>
          <Input
            type="file"
            p={0.5}
            accept="image/*"
            onChange={(e) => postDetails(e.target.files[0])}
          />
        </FormControl>
        <Button
          colorScheme="blue"
          width="100%"
          style={{ marginTop: 15 }}
          onClick={submitHandler}
          isLoading={loading}
        >
          Sign Up
        </Button>
      </VStack>
    );
}

export default Signup;
