import { isSameSenderMargin, isSameUser } from "../../config/ChatLogic";
import { ChatState } from "../Context/GlobalHooks";
import "../miscellaneous/styles.css";
import ScrollToBottom from "react-scroll-to-bottom";
import "../miscellaneous/styles.css";
function SingleChats({messages}){

    const {user} = ChatState();

    return (
      <div >
        {messages &&
          messages.map((m, i) => (
            <div style={{ display: "flex" }} key={m._id}>
              <span
                style={{
                  backgroundColor: `${
                    m.sender._id === user._id ? "white" : "#B9F5D0"
                  }`,
                  marginLeft: user._id == m.sender._id ? 50 : "auto",
                  marginRight: user._id == m.sender._id ? "auto" : 50,
                  marginTop: isSameUser(messages, m, i, user._id) ? 3 : 10,
                  //marginTop: "8px",
                  borderRadius: "10px",
                  padding: "5px 15px",
                  maxWidth: "75%",
                }}
              >
                {m.content}
              </span>
            </div>
          ))}
      </div>
    );
}

export default SingleChats;
