import { ViewIcon } from "@chakra-ui/icons";
import { IconButton, ModalOverlay,ModalContent,ModalHeader,ModalCloseButton,ModalBody,Box,FormControl,Input,Button,ModalFooter, useToast } from "@chakra-ui/react";
import UserBadgeItem from "./UserBadgeItem";
import UserListItem from "./UserList";
import {ChatState} from "../Context/GlobalHooks";
import axios from "axios";

function UpdateGroupChatModal(){
    
    const toast = useToast();
    const { user, selectedChat , setSelectedChat} = ChatState();

    const handleRemove = async(usertoremove)=>{
     if(selectedChat.groupAdmin._id !== user._id && usertoremove._id !== user._id ){
       toast({
         title: "Only admins can remove someone!",
         status: "error",
         duration: 5000,
         isClosable: true,
         position: "bottom",
       });
       return;
     }
     try {
       const config = {
         headers: {
            Authorization: `Bearer ${user.token}`,
         },
       };

       const {data} = await axios.put(`/api/chat/groupremove`,
       {
        chatId: selectedChat._id,
        userId: usertoremove._id,
       }
       ,config) 

       usertoremove._id === user._id ? setSelectedChat() : setSelectedChat(data);

     } catch (error) {}
    }

    return (
      <>
        <IconButton d={{ base: "flex" }} icon={<ViewIcon />} onClick={onOpen} />

        <Modal onClose={onClose} isOpen={isOpen} isCentered>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader
              fontSize="35px"
              fontFamily="Work sans"
              d="flex"
              justifyContent="center"
            >
              {selectedChat.chatname}
            </ModalHeader>

            <ModalCloseButton />
            <ModalBody d="flex" flexDir="column" alignItems="center">
              <Box w="100%" d="flex" flexWrap="wrap" pb={3}>
                {selectedChat.users.map((u) => (
                  <UserBadgeItem
                    key={u._id}
                    user={u}
                    admin={selectedChat.groupAdmin}
                    handleFunction={() => handleRemove(u)}
                  />
                ))}
              </Box>
              <FormControl d="flex">
                <Input
                  placeholder="Chat Name"
                  mb={3}
                  value={groupChatName}
                  onChange={(e) => setGroupChatName(e.target.value)}
                />
                <Button
                  variant="solid"
                  colorScheme="teal"
                  ml={1}
                  isLoading={renameloading}
                  onClick={handleRename}
                >
                  Update
                </Button>
              </FormControl>
              <FormControl>
                <Input
                  placeholder="Add User to group"
                  mb={1}
                  onChange={(e) => handleSearch(e.target.value)}
                />
              </FormControl>

              {loading ? (
                <Spinner size="lg" />
              ) : (
                searchResult?.map((user) => (
                  <UserListItem
                    key={user._id}
                    user={user}
                    handleFunction={() => handleAddUser(user)}
                  />
                ))
              )}
            </ModalBody>
            <ModalFooter>
              <Button onClick={() => handleRemove(user)} colorScheme="red">
                Leave Group
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    );
}

export default UpdateGroupChatModal;
